import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";

const DashBoard = ({ currentUser }) => {
  // const location = useLocation()
  // const userName = location.state.lastName
  const [greet, setGreet] = useState("");
  const getCurrentTime = () => new Date().getHours();

  const greetingTime = (hour) => {
    if (hour >= 5 && hour < 12) {
      return "Good Morning";
    } else if (hour >= 12 && hour < 18) {
      return "Good Afternoon";
    } else {
      return "Good Evening";
    }
  };

  useEffect(() => {
    const hour = getCurrentTime();
    const greeting = greetingTime(hour);
    setGreet(greeting);
  }, []);

  const rows = [{ FirstName: "Spongebob", LastName: "Squarepants", Title:"HR Admin", }];

  return (
    <>
      <h2>Home Page</h2>
      <p>{`${greet}, ${currentUser.firstName} ${currentUser.lastName}!`}</p>
      <TableContainer component={Paper}>
      <h1>Employee Rota</h1>
        <Table
          sx={{
            minWidth: 500,
          }}
          aria-label="simple table"
        >
          <TableHead>
            <TableRow>
              <TableCell>First Name</TableCell>

              <TableCell>Last Name</TableCell>
              <TableCell>Title</TableCell>
            </TableRow>
          </TableHead>

          <TableBody>
            {rows.map((row) => (
              <TableRow key={`${row["First Name"]}-${row["Last Name"]}`}>
                <TableCell component="th" scope="row">
                  {row["First Name"]}
                </TableCell>
                <TableCell align="right">{row["Last Name"]}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
};

export default DashBoard;
